      .replace(/^pod\//, '')
      // drop RS hash + optional pod-uniq suffix
      .replace(/-[a-z0-9]{8,10}(?:-[a-z0-9]{4,6})?$/i, '')
      .trim();
  }

  const depName = raw.match(/kind:\s*Deployment[\s\S]*?\n\s*name:\s*([-\w]+)/)?.[1];
  if (depName) return depName.trim();
})()
}}

