={{
(() => {
  const iid = $node["Prepare Incident Card"].json?.incident_id || '';
  const iidShort = iid.slice(0, 3);

  const u = $node["Confirm Fix in Slack"].json?.user || {};
  const userDisplay =
    u.profile?.real_name ||
    u.real_name ||
    u.profile?.display_name ||
    u.name ||
    u.username ||
    u.id ||
    '';

  const raw = String($node["Assemble Evidence"].json?.probe_summary || '');

  const sec = (title) => {
    const re = new RegExp(`---\\s*${title}\\s*---[\\s\\S]*?(?=\\n---|$)`, 'i');
    const m = raw.match(re);
    return m ? m[0] : '';
  };

  const eventsRaw = sec('LAST EVENTS');
  const eventsLines = eventsRaw
    .split('\n')
    .filter(l => /(Warning|Failed|BackOff|ErrImagePull|ImagePullBackOff)/i.test(l))
    .slice(-12) // en son 12 satƒ±r
    .join('\n')
    .trim();

  const logsRaw = sec('LAST LOGS');
  const logsBody = logsRaw.replace(/^[\s\S]*?---\s*LAST LOGS[^\n]*\n/i, '');
  const logsTail = logsBody
    .split('\n')
    .slice(-30)
    .join('\n')
    .trim();

  let signals = eventsLines || logsTail;
  if (!signals) {
    // fallback: RS TABLE ya da ilk 600 karakter
    const rs = sec('RS TABLE');
    signals = rs ? rs : raw.slice(0, 600);
  }
  const signalsTrim = signals.slice(0, 1600);
  const truncated = signals.length > 1600 ? '\n‚Ä¶(truncated)' : '';

  const summary = $node["Parse AI JSON"].json?.summary || 'N/A';
  const suggestion = $node["Parse AI JSON"].json?.suggestion_cmd || 'N/A';

  const userLine = userDisplay ? `\nUser: ${userDisplay}` : '';

  return `:warning: Manual resolution selected (Incident: ${iidShort}).${userLine}
Here is the latest context:
‚Ä¢ *AI Summary:* ${summary}
‚Ä¢ *AI Suggestion:* \`${suggestion}\`
‚Ä¢ *Signals:*
\`\`\`
${signalsTrim}${truncated}
\`\`\``;
})()
}}

